function switchToNewContent() {
    var defaultContent = document.getElementById("certifications");
    var newContent = document.getElementById("skills");
  
    // Hide default content and show new content
    defaultContent.style.display = "none";
    newContent.style.display = "block";
  }
  
  function switchToDefaultContent() {
    var defaultContent = document.getElementById("certifications");
    var newContent = document.getElementById("skills");
  
    // Hide new content and show default content
    defaultContent.style.display = "block";
    newContent.style.display = "none";
  }
  